            html();
        
        if (options_discussion) {
          vehicleModelDiscussion.html(options_discussion);
          vehicleModelDiscussion.prop("disabled", false);
        } 
        
        else {
          vehicleModelDiscussion.empty();
          vehicleModelDiscussion.prop("disabled", true);
        }
